package my_Project;

public class checkpositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int number = 1;
    
    if (number > 0) {
    	System.out.println(number + " is positive.");
    } else if (number == 0) {
    	System.out.println("number is zero.");
    } else {
    	System.out.println(number + " is negative.");
    }
	}

}
